mapper.select:
select

from model_info
where id != 1

  and id != #{modelType,jdbcType=VARCHAR}


  AND parent_id = #{parentId,jdbcType=INTEGER}


  AND one_model LIKE '%${name}%'


  AND id = #{id,jdbcType=VARCHAR}

order by id asc

SELECT COUNT(1)
FROM model_info
WHERE sort = #{sort,jdbcType=VARCHAR}
AND parent_id = #{parentId,jdbcType=VARCHAR}

select id,parent_id as parentId,model_name as modelName,one_model as oneModel, two_model as towModel,status
from model_info


    status = #{item,jdbcType=VARCHAR}

SELECT path as pathId,status as permission FROM model_info where id != 2 and id != 3
and (
id = #{item,jdbcType=VARCHAR}
)

select COUNT(1)
FROM model_info
WHERE status = #{status,jdbcType=VARCHAR};

mapper.insert:

insert into model_info (id, parent_id, model_name, 
  one_model, two_model, path, 
  sort, created_by, status
  )
values (#{id,jdbcType=INTEGER}, #{parentId,jdbcType=INTEGER}, #{modelName,jdbcType=VARCHAR}, 
  #{oneModel,jdbcType=VARCHAR}, #{twoModel,jdbcType=VARCHAR}, #{path,jdbcType=VARCHAR}, 
  #{sort,jdbcType=VARCHAR}, #{createdBy,jdbcType=VARCHAR}, #{status,jdbcType=VARCHAR}
  )
