mapper.delete:
delete from root_permission_info
where role = #{role,jdbcType=VARCHAR}
and permission != "0"

delete from root_permission_info
where path_id = #{pathId,jdbcType=VARCHAR}
and permission = #{status,jdbcType=VARCHAR}

mapper.update:
update root_permission_info
set path_id = #{path,jdbcType=VARCHAR}
where path_id = #{pathId,jdbcType=VARCHAR}
and permission = #{status,jdbcType=VARCHAR}
